void test_led_screen(void)
{	
  
    printf("before oled clear \r\n");
    OLED_Init();			   //初始化OLED  
    OLED_Clear(0);    
    // GUI_ShowString(20,0,"OpenHarmony", 16 ,1  );
    // GUI_DrawTriangel(0,30,10,10,30,30,16);
    // TEST_English();
    // TEST_Chinese();
    // GUI_DrawRectangle(0,0,127,63,16);
    // OLED_Display();
    // sleep(3);

    int x=0,y=0,x0=0;
    int a = 1,flag = 1;
    while(1){  
        OLED_Clear(0);
        GUI_ShowChinese(x,y,16,"王伟钊",1);
        GUI_ShowChinese(x,y+24,16,"黄梓烊",1);
        GUI_ShowString(0,y+16,"2020271040",8,1);
        GUI_ShowString(0,y+40,"2020192039",8,1);
        GUI_ShowString(x0,y+48,"OHOS",16,1);
        GUI_DrawBMP(64,y,64,64,logo,1);
        OLED_Display();
        if(x>=16){
            a = 0;
        }
        if(x<=0)
            a = 1;
        if(x0>=32)
            flag = 0;
        if(x0<=0)
            flag = 1;
        if(a){
            x=x+4;
        }
        else{
            x=x-4;
            }
        if(flag){
            x0=x0+2;
        }
        else{
            x0=x0-2;
            }
        sleep(1);
      }
}

